import { Calendar } from "grommet";
import PropTypes from "prop-types";
import React from "react";
const propTypes = {
  handleSelect: PropTypes.func.isRequired,
  dates: PropTypes.arrayOf(PropTypes.string).isRequired
};

const defaultProps = {};

const handleSelectDate = (dates, date, handleSelect) => {
  if (dates.includes(date.toString())) {
    return handleSelect(dates.filter(item => item !== date.toString()));
  }
  return handleSelect([...dates, date]);
};

export default function calendar({ handleSelect, dates }) {
  return (
    <section className="">
      <Calendar
        onSelect={date => handleSelectDate(dates, date, handleSelect)}
        size="medium"
        showAdjacentDays
        animate
        dates={dates}
      />
    </section>
  );
}

calendar.propTypes = propTypes;
calendar.defaultProps = defaultProps;
