export const handleSelectDate = (dates, date, handleSelect) => {
  const selectedDate = new Date(new Date(date).setHours(0, 0, 0, 0));
  if (dates && dates.hasOwnProperty(selectedDate)) {
    const newDates = { ...dates };
    delete newDates[selectedDate];
    return handleSelect(newDates);
  }
  return handleSelect({ ...dates, [selectedDate]: [] });
};

export const convertToTimeInterval = times => {
  const timeA = times[0];
  const timeB = times[1];

  const TwelveHourFormat = time => {
    if (time > 12) {
      return `${time - 12} pm`;
    }
    return `${time} am`;
  };

  return `${TwelveHourFormat(timeA)} to ${TwelveHourFormat(timeB)}`;
};
