export const handleSelectDate = (dates, date, handleSelect) => {
  if (dates && dates.includes(date.toString())) {
    return handleSelect(dates.filter(item => item !== date.toString()));
  }
  return handleSelect([
    ...dates,
    format(new Date(new Date().setDate(new Date().getDate() + 1)), "do MMMM")
  ]);
};
