import { Button } from "grommet";
import { Add } from "grommet-icons";
import { useState } from "react";
import Layout from "../components/Layout";
const propTypes = {};

const defaultProps = {};

export default function index() {
  const [items, setItems] = useState([{ id: 0 }]);
  return (
    <Layout>
      <section className="flex  vh-100 w-100">
        <aside className="title">
          <h1 className="ph4 ">Meet Box</h1>
        </aside>
        <div className="flex-grow-1 w-100 flex flex-column">
          <calendar />
          <div className="pa5   tc flex items-center justify-center grow">
            <Button
              icon={<Add />}
              primary
              label="Add Agenda Item"
              onClick={() => setItems([...items, { id: +new Date() }])}
            />
          </div>
        </div>
      </section>
      <style jsx>
        {`
          .title {
            writing-mode: vertical-rl;
            transform: rotate(180deg);
            text-align: center;
            font-size: 4rem;
            text-transform: uppercase;

            font-family: Futura, "Trebuchet MS", Arial, sans-serif;
            font-size: 23px;
            font-style: normal;
            font-variant: normal;
            font-weight: 700;
            line-height: 23px;
            color: #363d87;
          }
        `}
      </style>
    </Layout>
  );
}

index.propTypes = propTypes;
index.defaultProps = defaultProps;
