import { RangeSelector } from "grommet";
import PropTypes from "prop-types";
import React from "react";
import { SecondaryButton } from "../../../components/Buttons";
import Add from "../../../static/svg/Add";

const propTypes = {
  dates: PropTypes.arrayOf(PropTypes.string).isRequired,
  send: PropTypes.func.isRequired
};
const defaultProps = {};

export default function TimeBar({ dates, send }) {
  const [times, setTimes] = React.useState([1]);

  console.log("units", dates, times);
  return (
    <section className="w-100 h-75 tc">
      <h1 className="f1">Select up to 3 times...</h1>
      {dates &&
        dates.map(date => (
          <div key={date}>
            {times &&
              times.map(time => (
                <TimeBlock date={date} setTimes={setTimes} times={times} />
              ))}
          </div>
        ))}

      <SecondaryButton
        action={() => send("ADDED_NEW_DATE")}
        text="+ Add Another Date"
        className="mt5 h-25"
      />
    </section>
  );
}

TimeBar.propTypes = propTypes;
TimeBar.defaultProps = defaultProps;

export const convertToTimeInterval = times => {
  const timeA = times[0];
  const timeB = times[1];

  const TwelveHourFormat = time => {
    if (time > 12) {
      return `${time - 12} pm`;
    }
    return `${time} am`;
  };

  return `${TwelveHourFormat(timeA)} to ${TwelveHourFormat(timeB)}`;
};

function TimeBlock({ date, setTimes, times }) {
  const [values, handleChange] = React.useState([12, 13]);

  return (
    <>
      <h3 className="tc mt5 relative top-2">
        {date} <span className="f2 pl3">{convertToTimeInterval(values)}</span>
      </h3>
      <div className="h3 ma3 flex">
        <div className="w-100 pt0">
          <div className="flex justify-around relative top-2">
            {new Array(25).fill("").map((value, index) => {
              if (index === 0) {
                return (
                  <div
                    key={index}
                    className={`bn flex items-center justify-center w-100 z-1`}
                  >
                    <Add
                      className="grow pb2 pointer gray"
                      onClick={() => setTimes(times.concat(1))}
                    />
                  </div>
                );
              }
              return (
                <div
                  key={index}
                  className={`bn tc w-100 ${(index <= 5 || index >= 20) &&
                    "bg-light-gray"}`}
                >
                  <p className={`code pb2 tc`}>{index}</p>
                </div>
              );
            })}
          </div>
          <RangeSelector
            values={values}
            min={0}
            max={24}
            step={1}
            color="#7D52D8"
            className="relative  bottom-2 pt1"
            onChange={handleChange}
            round="small"
          />
        </div>
      </div>
    </>
  );
}
