import React from "react";
import Layout from "../../components/Layout";
import firebase from "../../sideEffects/firebase";

const propTypes = {};

const defaultProps = {};

const getInitialProps = async url => {
  const slug = url.query.slug;
  const meetings = await firebase
    .firestore()
    .collection("meetings")
    .doc(slug)
    .get()
    .then(doc => doc.exists && doc.data())
    .catch(error => error);

  return { meetings };
};

export default function index({ url, meetings, slug }) {
  // {url.query.slug}
  const options = Object.entries(JSON.parse(meetings.options));

  console.log(options);

  // {format(new Date(date), "do MMMM")}{" "}
  // <span className="f2 pl3">{convertToTimeInterval(time)}</span>
  return (
    <Layout>
      <h1 className="f1 tc">Pick a time...</h1>
      <section className="flex items-center justify-center">
        {options &&
          options.map(option => (
            <article className="mw5 center bg-white br3 pa3 pa4-ns mv3 ba b--black-10 pointer grow">
              <div className="tc">
                <h1 className="f4">{option[0]}</h1>
                <hr className="mw3 bb bw1 b--black-10" />
              </div>
              <p className="lh-copy measure center f6 black-70">{option[1]}</p>
            </article>
          ))}
      </section>
    </Layout>
  );
}

index.propTypes = propTypes;
index.defaultProps = defaultProps;
index.getInitialProps = getInitialProps;
