import { useState } from "react";
import Layout from "../components/Layout";
import Calendar from "../features/schedule/components/Calendar";
import LinkGenerator from "../features/schedule/components/LinkGenerator";
import TimeBar from "../features/schedule/components/TimeBar";
const propTypes = {};

const defaultProps = {};

export default function index() {
  const [dates, setDates] = useState([
    new Date(1000 * 60 * 60 * 24 + +new Date())
  ]);
  return (
    <Layout>
      <section className="flex flex-column flex-row-ns vh-100 w-100">
        <aside className="title">
          <h1 className="ph4 ">Meet Box</h1>
        </aside>
        <div className="flex-grow-1 w-100 flex flex-column">
          <Calendar handleSelect={setDates} dates={dates} />
          <TimeBar dates={dates} />
          <LinkGenerator />
        </div>
      </section>
      <style jsx>
        {`
          .title {
            writing-mode: vertical-rl;
            transform: rotate(180deg);
            text-align: center;
            font-size: 4rem;
            text-transform: uppercase;

            font-family: Futura, "Trebuchet MS", Arial, sans-serif;
            font-size: 23px;
            font-style: normal;
            font-variant: normal;
            font-weight: 700;
            line-height: 23px;
            color: #363d87;
          }
        `}
      </style>
    </Layout>
  );
}

index.propTypes = propTypes;
index.defaultProps = defaultProps;
