import { format } from "date-fns";
import { RangeSelector } from "grommet";
import PropTypes from "prop-types";
import React from "react";
import { SecondaryButton } from "../../../components/Buttons";
import Add from "../../../static/svg/Add";
const propTypes = {
  dates: PropTypes.arrayOf(PropTypes.string).isRequired,
  send: PropTypes.func.isRequired
};
const defaultProps = {};

export default function TimeBar({ dates, send }) {
  const [times, setTimes] = React.useState([1]);
  return (
    <section className="w-100 tc">
      {dates &&
        dates.map(date => (
          <div className="pa5" key={date}>
            {times && times.map(time => <TimeBlock date={date} />)}

            {/* <div className="tc">
              <SecondaryButton
                className="relative top-2"
                action={() => setTimes(times.concat(1))}
                text="Add another time on the same day."
              />
            </div> */}
          </div>
        ))}
      <SecondaryButton
        action={() => send("ADDED_NEW_DATE")}
        text="+ Add Another Date"
      />
    </section>
  );
}

TimeBar.propTypes = propTypes;
TimeBar.defaultProps = defaultProps;

function TimeBlock({ date }) {
  const [values, handleChange] = React.useState([12, 13]);

  return (
    <>
      <h3 className="tc">{format(new Date(date), "do MMMM")}</h3>
      <div className="h3 ma3 flex">
        <Add />
        <>
          <div className="flex justify-around relative top-2">
            {new Array(25).fill("").map((value, index) => (
              <div
                key={index}
                className={`bn tc w-100 ${(index <= 4 || index >= 19) &&
                  "bg-light-gray"}`}
              >
                <p className={`code pb2 tc`}>{index + 1}</p>
              </div>
            ))}
          </div>
          <RangeSelector
            values={values}
            min={0}
            max={24}
            step={1}
            color="#7D52D8"
            className="relative  bottom-2 pt1"
            onChange={handleChange}
            round="small"
          />
        </>
      </div>
    </>
  );
}
