import Layout from "../components/Layout";
const propTypes = {};

const defaultProps = {};

export default function index() {
  const items = new Array(10).fill(1);
  return (
    <Layout>
      <section className="flex  vh-100 w-100">
        <aside className="title">
          <h1 className="ph4">Agenda</h1>
        </aside>
        <div className="flex-grow-1 w-100 flex flex-column">
          {items.map((item, index) => (
            <AgendaItem index={index} />
          ))}
          <div className="h3  pointer tc flex items-center justify-center grow">
            <Button
              icon={<Add />}
              primary
              label="Add Agenda Item"
              onClick={() => {}}
            />
          </div>
        </div>
      </section>
      <style jsx>
        {`
          .title {
            writing-mode: vertical-rl;
            transform: rotate(180deg);
            text-align: center;
            font-size: 4rem;
            text-transform: uppercase;

            font-family: Futura, "Trebuchet MS", Arial, sans-serif;
            font-size: 23px;
            font-style: normal;
            font-variant: normal;
            font-weight: 700;
            line-height: 23px;
            color: #363d87;
          }
        `}
      </style>
    </Layout>
  );
}

