import { useMachine } from "@xstate/react";
import { useState } from "react";
import { Machine } from "xstate";
import Layout from "../components/Layout";
import Calendar from "../features/schedule/components/Calendar";
import TimeBar from "../features/schedule/components/TimeBar";

const scheduleMachine = Machine({
  id: "schedule",
  initial: "calendar",
  states: {
    calendar: {
      on: { CONFIRMED_DATES: "times" }
    },
    times: {
      on: {
        CONFIRMED_TIMES: "ready",
        ADDED_NEW_DATE: "calendar"
      }
    },
    ready: {
      on: { SELECTED_A_DATE: "confirmed" }
    },
    confirmed: {
      type: "final"
    }
  }
});

const propTypes = {};

const defaultProps = {};

export default function index() {
  const [dates, setDates] = useState([
    `${new Date(new Date().setDate(new Date().getDate() + 1))}`
  ]);

  const [current, send] = useMachine(scheduleMachine);
  return (
    <Layout>
      <section className="flex flex-column flex-row-ns vh-100 w-100">
        <aside className="title">
          <h1 className="ph4 ">Meet Box</h1>
        </aside>
        <div className="flex-grow-1 w-100 flex flex-column bg-blue">
          {current.value === "calendar" && (
            <>
              <h1 className="tc">Pick up to 3 dates...</h1>
              <Calendar handleSelect={setDates} dates={dates} />
              <button onClick={() => send("CONFIRMED_DATES")}>Next</button>
            </>
          )}

          {current.value === "times" && (
            <>
              <TimeBar dates={dates} send={send} />
              <button onClick={() => send("CONFIRMED_TIMES")}>Next</button>
            </>
          )}
        </div>
      </section>
      <style jsx>
        {`
          .title {
            writing-mode: vertical-rl;
            transform: rotate(180deg);
            text-align: center;
            font-size: 4rem;
            text-transform: uppercase;

            font-family: Futura, "Trebuchet MS", Arial, sans-serif;
            font-size: 23px;
            font-style: normal;
            font-variant: normal;
            font-weight: 700;
            line-height: 23px;
            color: #363d87;
          }
        `}
      </style>
    </Layout>
  );
}

index.propTypes = propTypes;
index.defaultProps = defaultProps;
