import { RangeSelector } from "grommet";
import PropTypes from "prop-types";
import React from "react";
const propTypes = {
  dates: PropTypes.arrayOf(PropTypes.string).isRequired,
  send: PropTypes.func.isRequired
};
const defaultProps = {};

export default function TimeBar({ dates, send }) {
  const [times, setTimes] = React.useState([1]);
  return (
    <section>
      {dates &&
        dates.map(date => (
          <div className="pa5" key={date}>
            <h3 className="tc">{date}</h3>
            {times && times.map(time => <TimeBlock date={date} />)}

            <div className="tc">
              <button
                className="relative top-2"
                onClick={() => setTimes(times.concat(1))}
              >
                Add another time
              </button>
            </div>
          </div>
        ))}
      <button onClick={() => send("")}>Add another Date</button>
    </section>
  );
}

TimeBar.propTypes = propTypes;
TimeBar.defaultProps = defaultProps;

function TimeBlock({ date }) {
  const [values, handleChange] = React.useState([12, 13]);

  return (
    <>
      <div className="h3 ma3">
        <div className="flex justify-around relative top-2">
          {new Array(25).fill("").map((value, index) => (
            <div
              key={index}
              className={`bn tc w-100 ${(index <= 4 || index >= 19) &&
                "bg-light-gray"}`}
            >
              <p className={`code pb2 tc`}>{index + 1}</p>
            </div>
          ))}
        </div>
        <RangeSelector
          values={values}
          min={0}
          max={24}
          step={1}
          color="#7D52D8"
          className="relative  bottom-2 pt1"
          onChange={handleChange}
          round="small"
        />
      </div>
    </>
  );
}
